const ChessApp = () => {
  return (
    <div>
      <ChessBoard />
    </div>
  )
}

const ChessBoard = () => {

  const initializeChessBoard = () => {
    const board = {}
    const letterIds = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']
    const numberIds = [1, 2, 3, 4, 5, 6, 7, 8]
    var dark = true
    for (var i = 0; i < letterIds.length; i++) {
      const squares = {}
      for (var j = 0; j < numberIds.length; j++) {
        squares[numberIds[j]] = {
          color: dark ? 'dark' : 'light',
          piece: null
        }
        dark = !dark
      }
      board[letterIds[i]] = squares
    }
    return board
  }

  const [chessBoard, setChessBoard] = [initializeChessBoard()]

