import './ChessApp.css';

const ChessApp = () => {
  return (
    <div>
      <ChessBoard />
    </div>
  )
}

const initializeChessBoard = () => {
  const board = {}
  const letterIds = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']
  const numberIds = [1, 2, 3, 4, 5, 6, 7, 8]
  var dark = true
  for (var letter of letterIds) {
    const squares = {}
    for (var number of numberIds) {
      squares[number] = {
        color: dark ? 'darkGray' : 'lightGray',
        piece: null
      }
      dark = !dark
    }
    dark = !dark
    board[letter] = squares
  }
  return board
}

const ChessBoard = () => {
  const [chessBoard, setChessBoard] = [initializeChessBoard()]

  console.log(chessBoard)
  console.log(chessBoard['a']['1']);

  var drawRow = 0
  return (
    <div style={{display:'grid', gridTemplateColumns:'1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr'}}>
      {Object.keys(chessBoard).map(letter => {
        var drawCol = 1
        drawRow++
        return (
          <div key={letter} style={{gridTemplateRows:'1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr'}}>
            {Object.keys(chessBoard[letter]).map(number => {
              const styleSquare = {
                backgroundColor: chessBoard[letter][number].color,
                gridColumnStart: drawRow,
                gridColumnEnd: drawRow + 1,
                gridRowStart: drawCol,
                gridRowEnd: drawCol + 1
              }
              drawCol++
              return (
                <div key={letter, number} style={styleSquare}>
                  {letter}{number}
                </div>
              )
            })}
          </div>
        )
      })}
    </div>
  )
}

export default ChessApp
