import './ChessApp.css';

import react, { useState, useEffect } from 'react'
import DrawBoard from './components/DrawBoard'
import ChessLogicManager from './managers/ChessLogicManager'
import InitializerManager from './managers/InitializerManager'

const ChessApp = () => {

  const [chessBoard, setChessBoard] = useState(InitializerManager.initializeChessBoard())

  const onPieceClickHandler = (chessPiece, letter, number) => {

    letter = parseInt(letter)
    number = parseInt(number)

    var possibleMoves = []

    chessBoard[letter][number].clicked = true

    switch (chessPiece.type) {
      case null:
        break;
      case 'k':
        possibleMoves = ChessLogicManager.getPossibleMovesKing(chessBoard, letter, number, setChessBoard)
        break;
      case 'q':
        break;
      case 'b':
        break;
      case 'n':
        break;
      case 'r':
        break;
      case 'p':
        possibleMoves = ChessLogicManager.getPossibleMovesPawn(chessBoard, letter, number)
        newChessBoard = ChessLogicManager.showPossibleMoves(chessBoard, possibleMoves)
        break;
      default:
        break;

      setChessBoard(newChessBoard)
    }

    console.log(letter, number, chessPiece);

  }

  return (
    <div>
      <DrawBoard chessBoard={chessBoard} onPieceClickHandler={onPieceClickHandler}/>
    </div>
  )
}


export default ChessApp
