import './ChessApp.css';
import queenlight from './images/Chess_qlt45.svg'

const ChessApp = () => {
  return (
    <div>
      <ChessBoard />
    </div>
  )
}

const initializeChessBoard = () => {
  const board = {}
  const letterIds = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']
  const numberIds = [1, 2, 3, 4, 5, 6, 7, 8]
  var dark = true
  for (var letter of letterIds) {
    const squares = {}
    for (var number of numberIds) {
      squares[number] = {
        color: dark ? 'darkGray' : 'lightGray',
        // piece: null
        piece: {
          type: 'queen',
          color: 'light'
        }
      }
      dark = !dark
    }
    dark = !dark
    board[letter] = squares
  }
  return board
}


const ChessBoard = () => {
  const [chessBoard, setChessBoard] = [initializeChessBoard()]

  console.log(chessBoard)
  console.log(chessBoard['a']['1']);

  const squarePx = 75

  return (
    <div>
      <DrawBoard chessBoard={chessBoard} squarePx={squarePx}/>
    </div>
  )
}

const DrawBoard = ({ chessBoard, squarePx }) => {
  return <DrawRows chessBoard={chessBoard} squarePx={squarePx} />
}

const DrawRows = ({ chessBoard, squarePx }) => {
  const rowStyles = {
    display:'grid', 
    gridTemplateRows:`${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px`
  }
  return (
    <div style={rowStyles}>
      {Object.keys(chessBoard).slice(0).reverse().map(letter => <DrawColumns key={letter} letter={letter} chessBoard={chessBoard} squarePx={squarePx}/>)}
    </div>
  )
}

const DrawColumns = ({ letter, chessBoard, squarePx }) => {
  var columnCounter = 1
  const columnStyles = {
    display:'grid', 
    gridTemplateColumns:`${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px`
  }
  return (
    <div style={columnStyles}>
      {Object.keys(chessBoard[letter]).map(number => {
        const squareStyle = {
          backgroundColor: chessBoard[letter][number].color,
          gridColStart: columnCounter,
          gridColEnd: columnCounter + 1
        }
        columnCounter++
        return <DrawSquare key={`${letter}${number}`} letter={letter} number={number} squareStyle={squareStyle} chessBoard={chessBoard}/>
      })}
    </div>
  )
}

const DrawSquare = ({ letter, number, squareStyle, chessBoard }) => {
  return (
    <div style={squareStyle}>
      {letter}{number}
      <DrawChessPiece letter={letter} number={number} chessBoard={chessBoard}/>
    </div>
  )
}

const DrawChessPiece = ({ letter, number, chessBoard }) => {
  const chessPiece = chessBoard[letter][number]['piece']

  if (chessPiece === null) {
    return null
  }
  return (
    <div>
      <img src={queenlight}/>
    </div>
  )
}

export default ChessApp
