import './ChessApp.css';

import react, { useState, useEffect } from 'react'

import kinglight from './images/Chess_klt45.svg'
import kingdark from './images/Chess_kdt45.svg'
import queenlight from './images/Chess_qlt45.svg'
import queendark from './images/Chess_qdt45.svg'
import bishoplight from './images/Chess_blt45.svg'
import bishopdark from './images/Chess_bdt45.svg'
import knightlight from './images/Chess_nlt45.svg'
import knightdark from './images/Chess_ndt45.svg'
import rooklight from './images/Chess_rlt45.svg'
import rookdark from './images/Chess_rdt45.svg'
import pawnlight from './images/Chess_plt45.svg'
import pawndark from './images/Chess_pdt45.svg'

import marker from './images/circle-64.png'


const pieceImages = {
  kl: kinglight,
  kd: kingdark,
  ql: queenlight,
  qd: queendark,
  bl: bishoplight,
  bd: bishopdark,
  nl: knightlight,
  nd: knightdark,
  rl: rooklight,
  rd: rookdark,
  pl: pawnlight,
  pd: pawndark 
}

const initialPieces = {
  1: 'r',
  2: 'n',
  3: 'b',
  4: 'q',
  5: 'k',
  6: 'b',
  7: 'n',
  8: 'r'
}

const numberToLetter = {
  1: 'a',
  2: 'b',
  3: 'c',
  4: 'd',
  5: 'e',
  6: 'f',
  7: 'g',
  8: 'h'
}

const initializeChessBoard = () => {
  const board = {}
  const letterIds = [1, 2, 3, 4, 5, 6, 7, 8]
  // 'a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'
  const numberIds = [1, 2, 3, 4, 5, 6, 7, 8]
  var dark = true
  for (var letter of letterIds) {
    const squares = {}
    for (var number of numberIds) {
      var colorPiece = null
      var typePiece = null

      if (number === 1 || number === 2) {
        colorPiece = 'l'
        if (number === 1 || number === 8) {
          typePiece = initialPieces[letter]
        }
        else {
          typePiece = 'p'
        }
      }
      else if (number === 7 || number === 8) {
        colorPiece = 'd'
        if (number === 1 || number === 8) {
          typePiece = initialPieces[letter]
        }
        else {
          typePiece = 'p'
        }
      }

      var pickedPiece = {
        type: typePiece,
        color: colorPiece,
        clicked: false,
        canMoveHere: false
      }
      // console.log(pickedPiece);

      squares[number] = {
        color: dark ? 'darkGray' : 'lightGray',
        piece: pickedPiece
        // piece: null
        // piece: {
        //   type: 'q',
        //   color: 'l'
        // }
      }
      dark = !dark
    }
    dark = !dark
    board[letter] = squares
  }
  console.log(board);
  return board
}



const ChessApp = () => {

  const [chessBoard, setChessBoard] = useState(initializeChessBoard())
  const [isDisplayingPossibleMoves, setisDisplayingPossibleMoves] = useState(false)

  const onPieceClickHandler = (chessPiece, letter, number) => {


    const isPieceAtPosition = (letterCheck, numberCheck) => {
      if (chessBoard[letterCheck][numberCheck].type !== null) {
        return true
      }
      return false
    }

    const isLocationWithinBounds = (letterCheck, numberCheck) => {
      return ((letterCheck >= 1 && letterCheck <= 8) && (numberCheck >= 1 && numberCheck <= 8))
    }

    const showPossibleMoves = (possibleMovesToShow) => {
      for (var i = 0; i < possibleMovesToShow.length; i++) {
        const letterIndexToShow = possibleMovesToShow[i][0]
        const numberIndexToShow = possibleMovesToShow[i][1]

        chessBoard[letterIndexToShow][numberIndexToShow].chessPiece.canMoveHere = true
      }
    }

    const getPossibleMovesKing = () => {
      const possibleMoves = []

      for (var i = -1; i <= 1; i++) {
        for (var j = -1; j <= 1; j++) {
          if (isLocationWithinBounds(letter + i, number + j)) {
            if (!isPieceAtPosition(letter + i, number + j))
              possibleMoves.push([letter + i, number + j])
          }
        }
      }
      console.log(possibleMoves)
      return possibleMoves 
    }

    const getPossibleMovesPawn = () => {
      const possibleMoves = []

      if (number === 2) {
        if (isLocationWithinBounds(letter, number + 2)) {
          if (!isPieceAtPosition(letter, number + 2)) {
            possibleMoves.push([letter, number + 2])
          }
        }
      }
      if (isLocationWithinBounds(letter, number + 1)) {
        if (!isPieceAtPosition(letter, number + 1)) {
          possibleMoves.push([letter, number + 1])
        }
      }
      console.log(possibleMoves)
      return possibleMoves 
    }

    switch (chessPiece.type) {
      case null:
        return null
      case 'k':
        const possibleMoves = getPossibleMovesKing()
        break;
      case 'q':
        break;
      case 'b':
        break;
      case 'n':
        break;
      case 'r':
        break;
      case 'p':

        break;
      default:
        break;
    }

    console.log(chessPiece);

  }

  return (
    <div>
      <DrawBoard chessBoard={chessBoard} onPieceClickHandler={onPieceClickHandler}/>
    </div>
  )
}

const DrawBoard = ({ chessBoard, onPieceClickHandler }) => {
  const LENGTH = Math.min(window.innerWidth, window.innerHeight)
  const BOARD_LENGTH = Math.round(LENGTH * 0.80)

  const styleBoard = {
    width: `${BOARD_LENGTH}px`,
    height: `${BOARD_LENGTH}px`,
    position: 'absolute',
    margin: 'auto',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
  }


  return (
    <div style={styleBoard}>
      <DrawRows chessBoard={chessBoard} onPieceClickHandler={onPieceClickHandler}/>
    </div>
  )
}

const DrawRows = ({ chessBoard, onPieceClickHandler }) => {
  const rowStyles = {
    display:'grid', 
    gridTemplateColumns:'12.5% 12.5% 12.5% 12.5% 12.5% 12.5% 12.5% 12.5%',
    height: '100%',
    width: '100%'
  }
  return (
    <div style={rowStyles}>
      {Object.keys(chessBoard).map(letter => <DrawColumns key={letter} letter={letter} chessBoard={chessBoard} onPieceClickHandler={onPieceClickHandler}/>)}
    </div>
  )
}

const DrawColumns = ({ letter, chessBoard, onPieceClickHandler }) => {
  var columnCounter = 1
  const columnStyles = {
    display:'grid', 
    gridTemplateRows:'12.5% 12.5% 12.5% 12.5% 12.5% 12.5% 12.5% 12.5%',
    gridTemplateColumns: '100%'
  }
  return (
    <div style={columnStyles}>
      {Object.keys(chessBoard[letter]).slice(0).reverse().map(number => {
        const squareStyle = {
          backgroundColor: chessBoard[letter][number].color,
          gridColStart: columnCounter,
          gridColEnd: columnCounter + 1,
          display: 'block',
          position: 'relative'
        }
        columnCounter++
        return <DrawSquare key={`${letter}${number}`} letter={letter} number={number} squareStyle={squareStyle} chessBoard={chessBoard} onPieceClickHandler={onPieceClickHandler}/>
      })}
    </div>
  )
}

const DrawSquare = ({ letter, number, squareStyle, chessBoard, onPieceClickHandler }) => {
  const chessPiece = chessBoard[letter][number].piece

  return (
    <div style={squareStyle} onClick={() => {onPieceClickHandler(chessPiece, letter, number)}}>
      <div style={{}}>
        {numberToLetter[letter]}{number}
      </div>
      <DrawChessPiece letter={letter} number={number} chessPiece={chessPiece} chessBoard={chessBoard}/>
    </div>
  )
}

const DrawChessPiece = ({ letter, number, chessBoard, chessPiece }) => {

  const imageDivStyle = {
    display: "flex",
    objectFit: "cover",
    justifyContent: "center",
    alignItems: "center",
    overflow: "hidden",
    position: 'absolute',
    top: 0,
    left: 0,
    minWidth: '100%',
    minHeight: '100%'
  }

  const imageStyle = {
    flexShrink: 0,
    minWidth: '100%',
    minHeight: '100%'
  }

  if (chessPiece.type === null) {
    if (chessPiece.clicked === true) {
      return (
        <div style={imageDivStyle}>
          <img src={marker} />
        </div>
      )
    }
    return null
  }

  return (
    <div style={imageDivStyle}>
      <img src={pieceImages[`${chessPiece.type}${chessPiece.color}`]} style={imageStyle} />
    </div>
  )
}

export default ChessApp
