const clearBoard = (chessBoard, setChessBoard) => {
    var newChessBoard = JSON.parse(JSON.stringify(chessBoard))
    for (var i = 1; i <= 8; i++) {
        for (var j = 1; j <= 8; j++) {
            newChessBoard[i][j].indicatorMoveHere = false
            newChessBoard[i][j].clicked = false
        }
    }
    setChessBoard(newChessBoard)
}

export default {
    clearBoard
}
