import { getSpaceUntilMaxLength } from "@testing-library/user-event/dist/utils"

const isPieceAtPosition = (chessBoard, letter, number) => {
    if (chessBoard[letter][number].piece.type === null) {
        return false
    }
    return true
}

const isEnemyPieceAtPosition = (chessBoard, letter, number, piece) => {
    if (chessBoard[letter][number].piece.type === null) {
        return false
    }
    if (chessBoard[letter][number].piece.color === piece.color) {
        return false
    }
    return true
}

const addCapturablePiecesPawn = (chessBoard, letter, number, piece) => {
    if (isLocationWithinBounds(letter - 1, number + 1)) {
        if (isEnemyPieceAtPosition(chessBoard, letter - 1, number + 1, chessPiece)) {
            possibleMoves.push([letter - 1, number + 1])
        }
    }    
}


const isLocationWithinBounds = (letter, number) => {
    return ((letter >= 1) && (letter <= 8)) && ((number >= 1) && (number <= 8))
}


const showPossibleMoves = (chessBoard, possibleMovesToShow, setChessBoard) => {
    const newChessBoard = chessBoard
    for (var i = 0; i < possibleMovesToShow.length; i++) {
        const letterIndexToShow = possibleMovesToShow[i][0]
        const numberIndexToShow = possibleMovesToShow[i][1]

        newChessBoard[letterIndexToShow][numberIndexToShow].indicatorMoveHere = true
        console.log(newChessBoard[letterIndexToShow][numberIndexToShow]); 
    }
    return newChessBoard
}

const addPossibleMove = (chessBoard, letter, number, possibleMoves, chessPiece) => {
    if (isLocationWithinBounds(letter, number)) {
        if (!isPieceAtPosition(chessBoard, letter, number)) {
            possibleMoves.push([letter, number])
            return true
        }
        else if (chessPiece.color !== chessBoard[letter][number].piece.color) {
            possibleMoves.push([letter, number])
            return false
        }
    }
    return false
}

const checkDiagonals = (chessBoard, letter, number, possibleMoves, chessPiece) => {
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter - i, number + i, possibleMoves, chessPiece)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter + i, number + i, possibleMoves, chessPiece)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter - i, number - i, possibleMoves, chessPiece)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter + i, number - i, possibleMoves, chessPiece)) {
            break
        }
    }
}

const checkVertHori = (chessBoard, letter, number, possibleMoves, chessPiece) => {
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter + i, number, possibleMoves, chessPiece)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter - i, number, possibleMoves, chessPiece)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter, number + i, possibleMoves, chessPiece)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, letter, number - i, possibleMoves, chessPiece)) {
            break
        }
    }
}

const getPossibleMovesKnight = (chessBoard, letter, number) => {
    const possibleMoves = []
    const checkPositions = [
        [letter + 2, number - 1], [letter + 2, number + 1], 
        [letter - 2, number - 1], [letter - 2, number + 1],
        [letter - 1, number - 2], [letter + 1, number - 2],
        [letter - 1, number + 2], [letter + 1, number + 2]
    ]
    const chessPiece = chessBoard[letter][number].piece

    for (var i = 0; i < checkPositions.length; i++) {
        addPossibleMove(chessBoard, checkPositions[i][0], checkPositions[i][1], possibleMoves, chessPiece)
    }
    return possibleMoves
}

const getPossibleMovesBishop = (chessBoard, letter, number) => {
    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    checkDiagonals(chessBoard, letter, number, possibleMoves, chessPiece)    

    return possibleMoves
}

const getPossibleMovesRook = (chessBoard, letter, number) => {
    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    checkVertHori(chessBoard, letter, number, possibleMoves, chessPiece)

    return possibleMoves
}

const getPossibleMovesKing = (chessBoard, letter, number) => {
    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    for (var i = -1; i <= 1; i++) {
        for (var j = -1; j <= 1; j++) {
            addPossibleMove(chessBoard, letter + i, number + j, possibleMoves, chessPiece)
        }
    }
    return possibleMoves 
}

const getPossibleMovesPawn = (chessBoard, letter, number) => {
    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    if (chessPiece.color === 'l') {
        if (number === 2) {
            addPossibleMove(chessBoard, letter, number + 2, possibleMoves, chessPiece) 
        }
        addPossibleMove(chessBoard, letter, number + 1, possibleMoves, chessPiece) 
        addCapturablePiecesPawn(chessBoard, letter - 1, number + 1, possibleMoves, chessPiece)
    }
    else {
        if (number === 7) {
            addPossibleMove(chessBoard, letter, number - 2, possibleMoves, chessPiece) 
        }
        addPossibleMove(chessBoard, letter, number - 1, possibleMoves, chessPiece) 
    }

    return possibleMoves 
}

const getPossibleMovesQueen = (chessBoard, letter, number) => {
    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    checkVertHori(chessBoard, letter, number, possibleMoves, chessPiece)
    checkDiagonals(chessBoard, letter, number, possibleMoves, chessPiece)

    return possibleMoves
}

export default {
    showPossibleMoves,
    getPossibleMovesKing,
    getPossibleMovesPawn,
    getPossibleMovesKnight,
    getPossibleMovesBishop,
    getPossibleMovesRook,
    getPossibleMovesQueen
}
