const isPieceAtPosition = (chessBoard, letter, number) => {
    if (chessBoard[letter][number].piece.type === null) {
        return false
    }
    return true
}

const isEnemyPieceAtPosition = (chessBoard, letter, number, piece) => {
    if (chessBoard[letter][number].piece.type === null) {
        return false
    }
    if (chessBoard[letter][number].piece.color === piece.color) {
        return false
    }
    return true
}

const checkValidChessBoard = (chessBoard, colorTurn) => {
    var kingCoords = findKingOfColor(chessBoard, colorTurn)

    for (var i = 1; i <= 8; i++) {
        for (var j = 1; j <= 8; j++) {
            if (colorTurn == 'l') {
                if (isPieceAtPosition(chessBoard, i, j)) {
                    if (chessBoard[i][j].piece.color == 'd') {
                        var possibleMoves = getPossibleMoves(chessBoard, i, j, chessBoard[i][j].piece)
                        if (possibleMoves.includes(kingCoords)) {
                            return false;
                        }
                    }
                }
            }
        }
    }
    return true
}

const findKingOfColor = (chessBoard, color) => {
    for (var i = 1; i <= 8; i++) {
        for (var j = 1; j <= 8; j++) {
