                if (chessBoard[i][j].piece.type === 'k') {
                    return [i, j]
                }
            }
        }
    }
    return null
}

const addCapturablePiecesPawn = (chessBoard, letter, number, possibleMoves, piece) => {
    if (isLocationWithinBounds(letter, number)) {
        if (isEnemyPieceAtPosition(chessBoard, letter, number, piece)) {
            possibleMoves.push([letter, number])
        }
    }    
}


const isLocationWithinBounds = (letter, number) => {
    return ((letter >= 1) && (letter <= 8)) && ((number >= 1) && (number <= 8))
}


const showPossibleMoves = (chessBoard, possibleMovesToShow, setChessBoard) => {
    const newChessBoard = chessBoard
    for (var i = 0; i < possibleMovesToShow.length; i++) {
        const letterIndexToShow = possibleMovesToShow[i][0]
        const numberIndexToShow = possibleMovesToShow[i][1]

        newChessBoard[letterIndexToShow][numberIndexToShow].indicatorMoveHere = true
        console.log(newChessBoard[letterIndexToShow][numberIndexToShow]); 
    }
    return newChessBoard
}

const addPossibleMove = (chessBoard, current, toMove, possibleMoves) => {
    var letter = toMove[0]
    var number = toMove[1]
    if (isLocationWithinBounds(letter, number)) {
        if (!isPieceAtPosition(chessBoard, letter, number)) {
            var newChessBoard = chessBoard
            newChessBoard[letter][number]
            
            possibleMoves.push([letter, number])
            return true
        }
        else if (chessPiece.color !== chessBoard[letter][number].piece.color) {
            if (chessPiece.type !== 'p') {
                possibleMoves.push([letter, number])
                return false
            }
        }
    }
    return false
}

const checkDiagonals = (chessBoard, letter, number, possibleMoves) => {
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, [letter, number], [letter - i, number + i], possibleMoves)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, [letter, number], [letter + i, number + i], possibleMoves)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, [letter, number], [letter - i, number - i] , possibleMoves)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, [letter, number], [letter + i, number - i], possibleMoves)) {
            break
        }
    }
