        if (!addPossibleMove(chessBoard, [letter, number], [letter, number + i], possibleMoves)) {
            break
        }
    }
    for (var i = 1; i <= 7; i++) {
        if (!addPossibleMove(chessBoard, [letter, number], [letter, number - i], possibleMoves)) {
