    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    if (chessPiece.color === 'l') {
        if (addPossibleMove(chessBoard, [letter, number], [letter, number + 1], possibleMoves) && number === 2) {
            addPossibleMove(chessBoard, letter, number + 2, possibleMoves, chessPiece) 
        }
        addCapturablePiecesPawn(chessBoard, letter - 1, number + 1, possibleMoves, chessPiece)
        addCapturablePiecesPawn(chessBoard, letter + 1, number + 1, possibleMoves, chessPiece)
    }
    else {
        if (addPossibleMove(chessBoard, letter, number - 1, possibleMoves, chessPiece) && number === 7) {
            addPossibleMove(chessBoard, letter, number - 2, possibleMoves, chessPiece) 
        }
        addCapturablePiecesPawn(chessBoard, letter - 1, number - 1, possibleMoves, chessPiece)
        addCapturablePiecesPawn(chessBoard, letter + 1, number - 1, possibleMoves, chessPiece)
    }
    return possibleMoves 
}

const getPossibleMovesQueen = (chessBoard, letter, number) => {
    const possibleMoves = []
    const chessPiece = chessBoard[letter][number].piece

    checkVertHori(chessBoard, letter, number, possibleMoves, chessPiece)
    checkDiagonals(chessBoard, letter, number, possibleMoves, chessPiece)

    return possibleMoves
