    var possibleMoves = []
    switch (chessPiece.type) {
        case null:
            break;
        case 'k':
            possibleMoves = getPossibleMovesKing(chessBoard, letter, number)
            break;
        case 'q':
            possibleMoves = getPossibleMovesQueen(chessBoard, letter, number)
            break;
        case 'b':
            possibleMoves = getPossibleMovesBishop(chessBoard, letter, number)
            break;
        case 'n':
            possibleMoves = getPossibleMovesKnight(chessBoard, letter, number)
            break;
        case 'r':
            possibleMoves = getPossibleMovesRook(chessBoard, letter, number)
            break;
        case 'p':
            possibleMoves = getPossibleMovesPawn(chessBoard, letter, number)
            break;
        default:
           break;
    }
    return possibleMoves
}

export default {
    showPossibleMoves,
    getPossibleMoves
}
