import './ChessApp.css';

const ChessApp = () => {
  return (
    <div>
      <ChessBoard />
    </div>
  )
}

const initializeChessBoard = () => {
  const board = {}
  const letterIds = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']
  const numberIds = [1, 2, 3, 4, 5, 6, 7, 8]
  var dark = true
  for (var letter of letterIds) {
    const squares = {}
    for (var number of numberIds) {
      squares[number] = {
        color: dark ? 'darkGray' : 'lightGray',
        piece: null
      }
      dark = !dark
    }
    dark = !dark
    board[letter] = squares
  }
  return board
}


const ChessBoard = () => {
  const [chessBoard, setChessBoard] = [initializeChessBoard()]

  console.log(chessBoard)
  console.log(chessBoard['a']['1']);

  const squarePx = 50

  return (
    <div>
      <DrawBoard chessBoard={chessBoard} squarePx={squarePx}/>
    </div>
  )
}

const DrawBoard = ({ chessBoard, squarePx }) => {
  return (
    <div style={{display:'grid', gridTemplateRows:`${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px`}}>
      {Object.keys(chessBoard).map(letter => {
        var drawCol = 1
        return (
          <DrawSquares letter={letter} chessBoard={chessBoard} squarePx={squarePx} drawCol={drawCol}/>
        )
      })}
    </div>
  )
}

const DrawSquares = ({ letter, chessBoard, squarePx, drawCol }) => {
  return (
    <div key={letter} style={{display:'grid', gridTemplateColumns:`${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px ${squarePx}px`}}>
      {Object.keys(chessBoard[letter]).map(number => {
        const styleSquare = {
          backgroundColor: chessBoard[letter][number].color,
          gridColStart: drawCol,
          gridColEnd: drawCol + 1
        }
        drawCol++
        return (
          <div key={`${letter}${number}`} style={styleSquare}>
            {letter}{number}
          </div>
        )
      })}
    </div>
  )

}

export default ChessApp
